import { useState } from "react";
import LoginImg from "../src/assets/jake-blucker-tMzCrBkM99Y-unsplash.jpg"

function App() {
  const handleSubmit = (event)=>{
    event.preventDefault()
    console.log(event.target[0].value)
  }
  return (
    <div className="grid grid-cols-1 sm:grid-cols-1 h-screen w-full bg-loginImg  bg-no-repeat bg-cover bg-center bg-fixed">


      <div className="flex flex-col justify-center">
        <form onSubmit={this.handleSubmit} className="max-w-[500px] w-full mx-auto p-20 rounded-xl">

          <div className="flex flex-col text-gray-300 py-2">
            <label>Password</label>
            <input ref={node => (this.inputNode = node)} name="password" className="rounded-lg bg-gray-700 mt-2 p-2 focus:border-blue-500 focus:bg-gray-800 bg-gradient-to-r from-gray-800 to-red-800 focus:outline-none" type="password"></input>
          </div>
          <button type="submit" className="w-full my-5 py-2 bg-teal-700 rounded-lg shadow-lg  bg-gradient-to-r from-gray-800 to-red-800 shadow-red font-bold text-yellow-50">Sign In</button>

        </form>
      </div>
    </div>
  );
}

export default App;
