import { useEffect, useState } from "react";

const useAuth = () => {

    const [auth,setAuth] = useState(null);
    const [user,setUser] = useState({});

    const isAuth = async() => {
        const user = localStorage.getItem('user')
        if (user){
            setUser(user);
            setAuth(true);
        }
        else{
            setAuth(false);
        }

    }


    useEffect(() => {
        isAuth();
    },[auth])

    return{
        auth:auth,
        user:user
    }
}
export default useAuth;
