  useEffect(()=>{
    const posts = postService.getAllPost().then((response)=>{
      response.map(post=>{
        console.log(post)
      })
    })
    
  })
  

  

  const animals = ["Dog", "Bird", "Cat", "Mouse", "Horse", "asd", "asdasd", "Horse", "asd", "asdasd"];
  return (
    
    <div class="grid max-h-screen place-items-center mt-40 mb-10">
    {animals.map(animal => (
      <div class=" rounded overflow-hidden border w-full lg:w-6/12 bg-white md:w-6/12 mx-3 md:mx-0 lg:mx-0">
        <div class="w-full flex justify-between p-3">
          <div class="flex">
            <div class="rounded-full h-8 w-8 bg-gray-500 flex items-center justify-center overflow-hidden">
              <img src="https://avatars0.githubusercontent.com/u/38799309?v=4" alt="profilepic"></img>
            </div>
            <span class="pt-1 ml-2 font-bold text-sm">braydoncoyer</span>
          </div>
          <span class="px-2 hover:bg-gray-300 cursor-pointer rounded"><i class="fas fa-ellipsis-h pt-2 text-lg"></i></span>
        </div>
        <img class="w-full bg-cover overflow-hidden" src="https://3.bp.blogspot.com/-Chu20FDi9Ek/WoOD-ehQ29I/AAAAAAAAK7U/mc4CAiTYOY8VzOFzBKdR52aLRiyjqu0MwCLcBGAs/s1600/DSC04596%2B%25282%2529.JPG"></img>
        <div class="px-3 pb-2">
          <div class="pt-2">
            <i class="far fa-heart cursor-pointer"></i>
            <span class="text-sm text-gray-400 font-medium">12 likes</span>
            <button class="text-sm text-green-500 font-bold ml-4 ">Like</button>
          </div>
          {/* <div class="pt-1">
            <div class="mb-2 text-sm">
              <span class="font-medium mr-2">braydoncoyer</span> Lord of the Rings is my favorite film-series. One day I'll make my way to New Zealand to visit the Hobbiton set!
            </div>
          </div>
          <div class="text-sm mb-2 text-gray-400 cursor-pointer font-medium">View all 14 comments</div>
          <div class="mb-2">
            <div class="mb-2 text-sm">
              <span class="font-medium mr-2">razzle_dazzle</span> Dude! How cool! I went to New Zealand last summer and had a blast taking the tour! So much to see! Make sure you bring a good camera when you go!
            </div>
          </div> */}
        </div>
      </div>
      ))}
    </div>

  )
}

export default HomeComponent
